import React, { useState } from 'react'
import './App.css'
// import axios from 'axios'
import { Container, Grid, TextField, makeStyles } from "@material-ui/core";


const useStyles = makeStyles((theme) => ({
    formControl: {
        margin: theme.spacing(1),
        minWidth: 120,
    },
    selectEmpty: {
        marginTop: theme.spacing(2),
    },
}));

const New = () => {

    const classes = useStyles()

    const [name, setname] = useState(" ");
    const [geo, setgeo] = useState(" ")


    const AddUserHandler = event => {
        event.preventDefault();
        const user = {
            name: name.toString(),
            geo: geo.toString(),
        };

        setname('');
        setgeo('');


        // axios.post('http://localhost:5000/app/newemp', user)
        //     .then(response => console.log(response.data))

    };




    const NameChange = event => {
        setname(event.target.value);
    }

    // const GeoChange = event => {
    //     setgeo(event.target.value);
    // }




    return (
        <div>
            <div >
                <form onSubmit={AddUserHandler}>
                    <div className="f">
                        <Container>
                            <Grid container spacing={2}>
                                <Grid item xs={6} md={3}>
                                    <TextField fullWidth value={name} onChange={NameChange} label="Name" variant="outlined" style={{ width: "200px" }} />
                                </Grid>
                            </Grid>
                        </Container>
                    </div>
                    <Grid item xs={6} md={3}>

                        <input type="submit" className="btn btn-primary" value="Submit" />

                    </Grid>
                </form>
            </div>
        </div>


    );
};
export default New
