import React, { useState } from 'react'
import './App.css'
import $ from 'jQuery';
// import axios from 'axios'
import { Container, Grid, TextField, makeStyles } from "@material-ui/core";


const useStyles = makeStyles((theme) => ({
    formControl: {
        margin: theme.spacing(1),
        minWidth: 120,
    },
    selectEmpty: {
        marginTop: theme.spacing(2),
    },
}));

const New = () => {

    const classes = useStyles()

    const [name, setname] = useState(" ");
    const [geo, setgeo] = useState(" ")


    const AddUserHandler = event => {
        event.preventDefault();
        const user = {
            name: name.toString(),
            geo: geo.toString(),
        };

        setname('');
        setgeo('');


        // axios.post('http://localhost:5000/app/newemp', user)
        //     .then(response => console.log(response.data))

    };

    
function getLocation(){
    var msg; 
  
    if('geolocation' in navigator){
      requestLocation();
    }else{
      msg = "Sorry, looks like your browser doesn't support geolocation";
      outputResult(msg); 
      $('.pure-button').removeClass('pure-button-primary').addClass('pure-button-success');
    }
  
  
    function requestLocation(){    
      var options = {
        enableHighAccuracy: false,
        timeout: 5000,
        maximumAge: 0
      };
    
      navigator.geolocation.getCurrentPosition(success, error, options); 
    
      function success(pos){
        var lng = pos.coords.longitude;
        var lat = pos.coords.latitude;
        msg = 'You appear to be at longitude: ' + lng + ' and latitude: ' + lat  + '<img src="https://maps.googleapis.com/maps/api/staticmap?zoom=15&size=300x300&maptype=roadmap&markers=color:red%7Clabel:A%7C' + lat + ',' + lng+ '&sensor=false">';
        outputResult(msg); 
        $('.pure-button').removeClass('pure-button-primary').addClass('pure-button-success');
      }
    

      function error(err){
        msg = 'Error: ' + err + ' :(';
        outputResult(msg); 
        $('.pure-button').removeClass('pure-button-primary').addClass('pure-button-error'); 
      }  
    } 
    function outputResult(msg){
      $('.result').addClass('result').html(msg);
    }
  } 



    const NameChange = event => {
        setname(event.target.value);
    }

    // const GeoChange = event => {
    //     setgeo(event.target.value);
    // }




    return (
        <div>
            <div className="f" >
                {/* onSubmit={AddUserHandler} */}
                <form >
                    <div className="f">
                        <Container>
                            <Grid container spacing={2}>
                                <Grid item xs={6} md={3}>
                                    <TextField fullWidth value={name} onChange={NameChange} label="Name" variant="outlined" style={{ width: "200px", color : "white" }} />
                                </Grid>
                            </Grid>
                        </Container>
                    </div>
                    <div className="f">
                    <Grid item xs={6} md={3}>

                        <input type="submit" onClick={getLocation} className="btn btn-primary" value="Submit" />

                    </Grid>
                    </div>
                </form>
            </div>
        </div>


    );
};
export default New
